preprocess.script:
({ content, filename }) => {
    if (!filename)
      return;
    const basename = path.basename(filename);
    if (basename.startsWith("+page.") || basename.startsWith("+layout.")) {
      const match = content.match(options_regex);
      if (match) {
        const fixed = basename.replace(".svelte", "(.server).js/ts"), message = `\n${colors.bold().red(path.relative(".", filename))}\n` + `\`${match[1]}\` will be ignored \u2014 move it to ${fixed} instead. See https://kit.svelte.dev/docs/page-options for more information.`;
        if (!warned.has(message)) {
          console.log(message);
          warned.add(message);
        }
      }
    }
  }

preprocess.markup:
({ content, filename }) => {
    if (!filename)
      return;
    const basename = path.basename(filename), has_children = content.includes("<slot") || isSvelte5Plus() && content.includes("{@render");
    if (basename.startsWith("+layout.") && !has_children) {
      const message = `\n${colors.bold().red(path.relative(".", filename))}\n` + `\`<slot />\`${isSvelte5Plus() ? " or `{@render ...}` tag" : ""}` + " missing \u2014 inner content will not be rendered";
      if (!warned.has(message)) {
        console.log(message);
        warned.add(message);
      }
    }
  }
